import string

HLNFeed = '<?xml version="1.0" encoding="utf-8"?><rss version="2.0"><channel><title>Het Nieuwsblad : Economie</title><link>https://www.nieuwsblad.be/nieuws/economie</link><description>Het Nieuwsblad : Economie</description><language>nl-BE</language><lastBuildDate>Thu, 02 Mar 2023 10:13:22 +0100</lastBuildDate><image><url>https://static.nieuwsblad.be/extra/assets/img/logo-ds-rss.gif</url><title>Het Nieuwsblad : Economie</title><link>https://www.nieuwsblad.be/nieuws/economie</link></image><ttl>2</ttl><pubDate>Thu, 02 Mar 2023 10:13:22 GMT</pubDate><item><guid isPermaLink="true">https://www.nieuwsblad.be/cnt/dmf20230301_96498996</guid><link>https://www.nieuwsblad.be/cnt/dmf20230301_96498996</link><title>Proximus haalt 500 miljoen euro op met bedrijfsobligatie</title><description>&lt;p&gt;Telecombedrijf Proximus heeft met de uitgifte van een bedrijfsobligatie 500 miljoen euro opgehaald bij institutionele beleggers. De opbrengst zal onder meer worden gebruikt om bestaande schulden te herfinancieren, zo meldt het bedrijf woensdag nabeurs.&lt;/p&gt</description><pubDate>Wed, 01 Mar 2023 19:00:14 +0100</pubDate><enclosure url="https://static.nieuwsblad.be/Assets/Images_Upload/2023/03/01/ae969d6b-5b79-4bd3-801a-6d9ca31791a3.jpg" type="image/jpeg" /></item><item><guid isPermaLink="true">https://www.nieuwsblad.be/cnt/dmf20230301_95712366</guid><link>https://www.nieuwsblad.be/cnt/dmf20230301_95712366</link><title>Energiewaakhond maakt werk van overwinstbelasting elektriciteitsproducenten</title><description>&lt;p&gt;Federale energiewaakhond Creg heeft een belangrijke eerste stap genomen richting de inning van de overwinstbelasting bij de producenten van elektriciteit. Die heffing moet de federale overheid dit en volgend jaar samen een miljard euro opleveren.&lt;/p&gt;</description><pubDate>Wed, 01 Mar 2023 16:49:18 +0100</pubDate><enclosure url="https://static.nieuwsblad.be/Assets/Images_Upload/2023/03/01/2516ae9b-c304-4914-bbeb-d629ca2ce2a6.jpg" type="image/jpeg" /></item><item><guid isPermaLink="true">https://www.nieuwsblad.be/cnt/dmf20230301_95441481</guid><link>https://www.nieuwsblad.be/cnt/dmf20230301_95441481</link><title>Child Focus wil eigen versie van ‘Take it down’ tegen verspreiding naaktbeelden</title><description>&lt;p&gt;Child Focus, de stichting voor vermiste en seksueel uitgebuite kinderen, reageert enthousiast op het Amerikaanse initiatief ‘Take it down’, waarbij online platformen als Facebook, OnlyFans en Pornhub zich ertoe verbinden om expliciete beelden van minderjarigen te verwijderen als die circuleren op hun platformen. “De industrie moet haar verantwoordelijkheid opnemen.”&lt;/p&gt;</description><pubDate>Wed, 01 Mar 2023 15:53:14 +0100</pubDate><enclosure url="https://static.nieuwsblad.be/Assets/Images_Upload/2023/03/01/3e08709f-c53c-4b0b-97ea-1385160d0c79.jpg" type="image/jpeg" /></item><item><guid isPermaLink="true">https://www.nieuwsblad.be/cnt/dmf20230301_95344391</guid><link>https://www.nieuwsblad.be/cnt/dmf20230301_95344391</link><title>Lufthansa plant dubbelbed in vliegtuigen</title><description>&lt;p&gt;De Duitse luchtvaartmaatschappij Lufthansa introduceert vanaf 2024 een nieuw luxeproduct op langeafstandsvluchten: een volledig afsluitbare suite voor twee personen, met mogelijkheid om een dubbelbed te ontvouwen. Zustermaatschappij Brussels Airlines heeft geen plannen in die richting.&lt;/p&gt;</description><pubDate>Wed, 01 Mar 2023 15:39:14 +0100</pubDate><enclosure url="https://static.nieuwsblad.be/Assets/Images_Upload/2023/02/17/6403533b-f67a-4f3c-8f54-91a916ea6b1d.jpg" type="image/jpeg" /></item><item><guid isPermaLink="true">https://www.nieuwsblad.be/cnt/dmf20230301_95225250</guid><link>https://www.nieuwsblad.be/cnt/dmf20230301_95225250</link><title>Amerikaanse farmareus Eli Lilly verlaagt insulineprijs drastisch</title><description>&lt;p&gt;Het Amerikaanse farmabedrijf Eli Lilly gaat de kostprijs van zijn meest voorgeschreven insulines met 70 procent verminderen. Dat heeft het bedrijf woensdag aangekondigd. Eli Lilly zegt met de ingreep insuline toegankelijker te willen maken voor iedereen. &lt;/p&gt;</description><pubDate>Wed, 01 Mar 2023 15:18:04 +0100</pubDate><enclosure url="https://static.nieuwsblad.be/Assets/Images_Upload/2023/03/01/eee576c7-877e-4f48-a6d4-df2eadfd9790.jpg" type="image/jpeg" /></item><item><guid isPermaLink="true">https://www.nieuwsblad.be/cnt/dmf20230301_94438190</guid><link>https://www.nieuwsblad.be/cnt/dmf20230301_94438190</link><title>Storingen bij Twitter lijken van de baan</title><description>&lt;p&gt;De storingen bij sociale netwerksite Twitter lijken opgelost. Woensdagochtend bleken heel wat gebruikers wereldwijd storingen te melden, rond 13.45 uur lijken ze alvast in ons land opgelost te zijn.&lt;/p&gt;</description><pubDate>Wed, 01 Mar 2023 14:19:21 +0100</pubDate><enclosure url="https://static.nieuwsblad.be/Assets/Images_Upload/2023/03/01/cfd5b919-54c5-42a9-b6d5-35776e8cb45d.jpg" type="image/jpeg" /></item><item><guid isPermaLink="true">https://www.nieuwsblad.be/cnt/dmf20230301_94757136</guid><link>https://www.nieuwsblad.be/cnt/dmf20230301_94757136</link><title>TikTok gaat minderjarigen waarschuwen na uur kijken</title><description>&lt;p&gt;De Chinese app voor korte video’s TikTok gaat in de komende weken een automatische timer invoeren die minderjarige gebruikers na 60 minuten een waarschuwing stuurt. Ze moeten dan hun paswoord invoeren om verder te kunnen kijken, zo meldt het bedrijf.&lt;/p&gt;</description><pubDate>Wed, 01 Mar 2023 14:11:11 +0100</pubDate><enclosure url="https://static.nieuwsblad.be/Assets/Images_Upload/2023/03/01/3bac932e-05f9-461b-b150-f53d3fd44ca9.jpg" type="image/jpeg" /></item><item><guid isPermaLink="true">https://www.nieuwsblad.be/cnt/dmf20230301_94638740</guid><link>https://www.nieuwsblad.be/cnt/dmf20230301_94638740</link><title>Only Fans-bedrijf moet meer btw betalen en dat zullen de gebruikers voelen</title><description>&lt;p&gt;Fenix International, het moederbedrijf van Only Fans, is op de vingers getikt door het Europees Hof van Justitie. Het bedrijf is meer btw verschuldigd aan de Britse overheid dan dat het tot nu toe betaalde. En dat zal in de praktijk vooral door de gebruikers gevoeld worden. &lt;/p&gt;</description><pubDate>Wed, 01 Mar 2023 13:53:05 +0100</pubDate><enclosure url="https://static.nieuwsblad.be/Assets/Images_Upload/2023/03/01/b8399da5-ab65-445f-ac03-71ab76003775.jpg" type="image/jpeg" /></item><item><guid isPermaLink="true">https://www.nieuwsblad.be/cnt/dmf20230301_94483632</guid><link>https://www.nieuwsblad.be/cnt/dmf20230301_94483632</link><title>Prijsverhoging van Durex-condooms schrikt klanten af</title><description>&lt;p&gt;Het moederbedrijf van condoommerk Durex, Reckitt Benckiser, gaat een uitdagend jaar tegemoet. De prijsverhogingen van de afgelopen tijd lijken consumenten af te schrikken, meldt de Britse groep. De prijsverhogingen waren nodig om de hoge inflatie en gestegen grondstofkosten op te vangen. Desondanks denkt het bedrijf de winstgevendheid licht te kunnen verbeteren.&lt;/p&gt;</description><pubDate>Wed, 01 Mar 2023 13:26:16 +0100</pubDate><enclosure url="https://static.nieuwsblad.be/Assets/Images_Upload/2023/03/01/ca15ad1c-5e63-427b-a9f2-3d1f17fa7096.jpg" type="image/jpeg" /></item><item><guid isPermaLink="true">https://www.nieuwsblad.be/cnt/dmf20230301_94385159</guid><link>https://www.nieuwsblad.be/cnt/dmf20230301_94385159</link><title>Staking uitgebroken in versdepot van Delhaize</title><description>&lt;p&gt;In het distributiecentrum voor verse producten van Delhaize in Asse is een staking uitgebroken. Dat meldt De Tijd en de warenhuisketen bevestigt het nieuws.&lt;/p&gt;</description><pubDate>Wed, 01 Mar 2023 13:09:27 +0100</pubDate><enclosure url="https://static.nieuwsblad.be/Assets/Images_Upload/2023/02/16/7de501fe-4353-4c7f-b9c6-c7edc834756b.jpg" type="image/jpeg" /></item><item><guid isPermaLink="true">https://www.nieuwsblad.be/cnt/dmf20230301_93408878</guid><link>https://www.nieuwsblad.be/cnt/dmf20230301_93408878</link><title>Belgische wijnboeren hadden recordjaar</title><description>&lt;p&gt;Nooit was de Belgische wijnproductie zo groot als in 2022, zo meldt de federale overheidsdienst Economie. Tegenover het slechte jaar 2021 verdubbelde de productie tot 3 miljoen liter. Het vorige recordjaar was 2018 met 2 miljoen liter.&lt;/p&gt;</description><pubDate>Wed, 01 Mar 2023 10:23:16 +0100</pubDate><enclosure url="https://static.nieuwsblad.be/Assets/Images_Upload/2023/03/01/d3f4a116-f865-4b81-8bd1-addb7fdfba33.jpg" type="image/jpeg" /></item><item><guid isPermaLink="true">https://www.nieuwsblad.be/cnt/dmf20230301_93132906</guid><link>https://www.nieuwsblad.be/cnt/dmf20230301_93132906</link><title>Dan toch geen gratis water in Waalse horeca: “Gratis bestaat niet”</title><description>&lt;p&gt;De belofte van de Waalse regering uit haar beleidsverklaring van 2019 om de horeca te verplichten gratis water te voorzien voor klanten, wordt niet nagekomen. Dat schrijft La Libre.&lt;/p&gt;</description><pubDate>Wed, 01 Mar 2023 09:36:04 +0100</pubDate><enclosure url="https://static.nieuwsblad.be/Assets/Images_Upload/2023/03/01/991d61e5-5402-49e0-b5e4-e52cfbfe9623.jpg" type="image/jpeg" /></item><item><guid isPermaLink="true">https://www.nieuwsblad.be/cnt/dmf20230301_91717055</guid><link>https://www.nieuwsblad.be/cnt/dmf20230301_91717055</link><title>Bedrijven keerden recordbedrag dividenden uit in 2022</title><description>&lt;p&gt;Bedrijven hebben wereldwijd het afgelopen jaar 1.560 miljard dollar aan dividenden uitgekeerd. Dat is een nieuw record, zo blijkt woensdag uit een rapport van vermogensbeheerder Janus Henderson.&lt;/p&gt;</description><pubDate>Wed, 01 Mar 2023 05:32:46 +0100</pubDate><enclosure url="https://static.nieuwsblad.be/Assets/Images_Upload/2023/02/28/dbf65601-2ac9-4405-9636-3bec3f9cb29f.jpg" type="image/jpeg" /></item><item><guid isPermaLink="true">https://www.nieuwsblad.be/cnt/dmf20230228_96916470</guid><link>https://www.nieuwsblad.be/cnt/dmf20230228_96916470</link><title>Multinationals en aandeelhouders profiteren van hoge voedingsprijzen: genoeg om de 230 miljoen armsten eten te geven</title><description>&lt;p&gt;De twintig grootste multinationals konden dankzij de hoge voedingsprijzen 53,5 miljard dollar uitkeren aan hun aandeelhouders. Met dat bedrag kun je de 230 miljoen armsten voeden, stelt Greenpeace in een studie.&lt;/p&gt;</description><pubDate>Wed, 01 Mar 2023 03:00:00 +0100</pubDate><enclosure url="https://static.nieuwsblad.be/Assets/Images_Upload/2023/02/28/28784c85-c3da-4e39-9ccd-67f0a815ec06.jpg" type="image/jpeg" /></item><item><guid isPermaLink="true">https://www.nieuwsblad.be/cnt/dmf20230228_96639938</guid><link>https://www.nieuwsblad.be/cnt/dmf20230228_96639938</link><title>Waarom ‘stille chic’ de luxetrend van dit jaar wordt</title><description>&lt;p&gt;Wie dure kleren draagt, toont dat graag. Toch? Nu niet meer, want dé trend voor 2023 is ‘quiet luxury’ of stille luxe. Het exact tegenovergestelde van de ‘luide’ luxemode met grote logo’s. Geen schreeuwerige sneakers van Balenciaga of patserige riemen met de dubbele ‘G’ van Gucci. “Het is luxe die je voelt, maar niet tentoonstelt.”&lt;/p&gt;</description><pubDate>Wed, 01 Mar 2023 03:00:00 +0100</pubDate><enclosure url="https://static.nieuwsblad.be/Assets/Images_Upload/2023/03/01/9c0f9476-004e-4bca-85e0-ec21a497cbc2.jpg" type="image/jpeg" /></item><item><guid isPermaLink="true">https://www.nieuwsblad.be/cnt/dmf20230228_97241570</guid><link>https://www.nieuwsblad.be/cnt/dmf20230228_97241570</link><title>Federale overheid doet deel van belang in BNP Paribas van de hand</title><description>&lt;p&gt;De federale overheid wil een deel van het belang in de Franse bank BNP Paribas verkopen. Dat melden de krant De Tijd en het financiële persagentschap Bloomberg. De woordvoerder van premier Alexander De Croo bevestigt de operatie.&lt;/p&gt;</description><pubDate>Tue, 28 Feb 2023 21:04:22 +0100</pubDate><enclosure url="https://static.nieuwsblad.be/Assets/Images_Upload/2023/02/28/9b38c142-2e8a-4820-8cd0-02060ffe106a.jpg" type="image/jpeg" /></item><item><guid isPermaLink="true">https://www.nieuwsblad.be/cnt/dmf20230228_96834621</guid><link>https://www.nieuwsblad.be/cnt/dmf20230228_96834621</link><title>Franse Spotify-concurrent Deezer maakte 168 miljoen euro verlies vorig jaar</title><description>&lt;p&gt;De Franse muziekstreamingdienst Deezer heeft vorig jaar een nettoverlies geleden van 168 miljoen euro, meer dan in het jaar ervoor toen het bedrijf 123 miljoen euro in het rood ging. Dat blijkt uit dinsdag gepubliceerde financiële resultaten.&lt;/p&gt;</description><pubDate>Tue, 28 Feb 2023 19:57:41 +0100</pubDate><enclosure url="https://static.nieuwsblad.be/Assets/Images_Upload/2023/02/28/4adc28c5-7a58-44bf-8316-27cae6c22d87.jpg" type="image/jpeg" /></item><item><guid isPermaLink="true">https://www.nieuwsblad.be/cnt/dmf20230228_95714403</guid><link>https://www.nieuwsblad.be/cnt/dmf20230228_95714403</link><title>Tesla gaat fabriek bouwen in Mexico</title><description>&lt;p&gt;De Amerikaanse autobouwer Tesla zal een fabriek openen in het Mexicaanse Monterrey. Dat heeft de Mexicaanse president Andres Manuel Lopez Obrador dinsdag aangekondigd. Volgens de president zal de investering veel jobs opleveren. Tesla zelf zou woensdag meer details geven.&lt;/p&gt;</description><pubDate>Tue, 28 Feb 2023 16:50:41 +0100</pubDate><enclosure url="https://static.nieuwsblad.be/Assets/Images_Upload/2023/02/28/f76f602f-6c41-452e-a395-9ac05b7bab8b.jpg" type="image/jpeg" /></item><item><guid isPermaLink="true">https://www.nieuwsblad.be/cnt/dmf20230228_95142872</guid><link>https://www.nieuwsblad.be/cnt/dmf20230228_95142872</link><title>Ook in maart geen vast energiecontract bij marktleider Engie</title><description>&lt;p&gt;Engie - veruit de belangrijkste leverancier van elektriciteit en aardgas in ons land - gaat voorlopig ook in maart geen vaste contracten aanbieden. Dat bevestigt een woordvoerster van het bedrijf. Engie zegt wel de markt continu te monitoren, maar voorlopig biedt het bedrijf enkel variabele contracten aan.&lt;/p&gt;</description><pubDate>Tue, 28 Feb 2023 15:15:44 +0100</pubDate><enclosure url="https://static.nieuwsblad.be/Assets/Images_Upload/2023/02/28/a9da6be0-9820-4b35-9ca0-a59ec4631541.jpg" type="image/jpeg" /></item><item><guid isPermaLink="true">https://www.nieuwsblad.be/cnt/dmf20230228_94054631</guid><link>https://www.nieuwsblad.be/cnt/dmf20230228_94054631</link><title>Aandeelhouder van Tesla wil schadevergoeding na problemen met autopiloot</title><description>&lt;p&gt;Een aandeelhouder van de Amerikaanse fabrikant van elektrische wagens Tesla wil met een groepsclaim in de Verenigde Staten een compensatie afdwingen voor de koersverliezen van het aandeel van het bedrijf, als gevolg van ongevallen en problemen met het rijhulpsysteem ‘Autopilot’. Dat blijkt uit een klacht die maandag bij een rechtbank in Californië werd ingediend. In de klacht worden Tesla, Tesla-topman Elon Musk en andere topmanagers ervan beschuldigd de mogelijkheden van het systeem voor autonoom rijden met valse en misleidende beweringen overdreven voorgesteld te hebben.&lt;/p&gt;</description><pubDate>Tue, 28 Feb 2023 12:14:12 +0100</pubDate><enclosure url="https://static.nieuwsblad.be/Assets/Images_Upload/2023/02/28/a8ac1a7a-7c3d-4d13-8be1-b867f024812f.jpg" type="image/jpeg" /></item><item><guid isPermaLink="true">https://www.nieuwsblad.be/cnt/dmf20230228_94068586</guid><link>https://www.nieuwsblad.be/cnt/dmf20230228_94068586</link><title>Dreigen nieuwe records? “Heropening van China is grootste bedreiging voor gasprijs”</title><description>&lt;p&gt;Het is niet ondenkbaar dat de aardgasprijzen de komende maanden opnieuw naar recordhoogtes stijgen. Dat zal vooral bepaald worden door de vraag in China, nu dat land de strenge coronamaatregelen heeft laten vallen. Dat staat dinsdag in een rapport van het Internationaal Energieagentschap.&lt;/p&gt;</description><pubDate>Tue, 28 Feb 2023 12:00:11 +0100</pubDate><enclosure url="https://static.nieuwsblad.be/Assets/Images_Upload/2023/02/28/dbf65601-2ac9-4405-9636-3bec3f9cb29f.jpg" type="image/jpeg" /></item><item><guid isPermaLink="true">https://www.nieuwsblad.be/cnt/dmf20230228_93720415</guid><link>https://www.nieuwsblad.be/cnt/dmf20230228_93720415</link><title>Onlyfans verslikt zich in Europese btw-richtlijn</title><description>&lt;p&gt;Het bekende online platform Onlyfans, waar gebruikers tegen betaling (live)video’s en foto’s van zogenaamde creators kunnen bekijken, is meer btw verschuldigd aan de Britse overheid dan het tot nu aannam. Dat blijkt uit een arrest van het Europees Hof van Justitie.&lt;/p&gt;</description><pubDate>Tue, 28 Feb 2023 11:15:57 +0100</pubDate><enclosure url="https://static.nieuwsblad.be/Assets/Images_Upload/2023/02/28/c90c5e14-1e6f-484b-8373-2dadf0e91bd0.jpg" type="image/jpeg" /></item><item><guid isPermaLink="true">https://www.nieuwsblad.be/cnt/dmf20230228_93630175</guid><link>https://www.nieuwsblad.be/cnt/dmf20230228_93630175</link><title>Nationale Bank: “Belgische economie groeide in 2022 met 3,1 procent”</title><description>&lt;p&gt;De Belgische economie is in het vierde kwartaal van 2022 met 0,1 procent gestegen ten opzichte van het voorgaande kwartaal, zo bevestigt de Nationale Bank dinsdag een eerdere flashraming. Over heel 2022 groeide het Belgische binnenlands product (bbp) met 3,1 procent. Het waren vooral de consumptie van de gezinnen en de buitenlandse handel die de motor deden draaien, zo blijkt.&lt;/p&gt;</description><pubDate>Tue, 28 Feb 2023 11:01:13 +0100</pubDate><enclosure url="https://static.nieuwsblad.be/Assets/Images_Upload/2023/02/23/69b3a561-a865-40df-9f08-e756b5fe7d02.jpg" type="image/jpeg" /></item><item><guid isPermaLink="true">https://www.nieuwsblad.be/cnt/dmf20230228_91418141</guid><link>https://www.nieuwsblad.be/cnt/dmf20230228_91418141</link><title>Huurprijzen in 2022 sneller gestegen in Vlaanderen, maar minder dan helft van inflatie </title><description>&lt;p&gt;Nieuwe verhuringen zijn in Vlaanderen in 2022 voor het eerst in vele jaren minder snel duurder geworden dan lopende huurcontracten. Normaal gezien is die trend omgekeerd. Dat blijkt uit de nieuwste editie van de Huurbarometer van CIB Vlaanderen, dat de immosector, syndici en de rentmeesters vertegenwoordigt. De gemiddelde huurprijs in Vlaanderen steeg van 778 euro in 2021 tot 815 euro in 2022.&lt;/p&gt;</description><pubDate>Tue, 28 Feb 2023 04:10:18 +0100</pubDate><enclosure url="https://static.nieuwsblad.be/Assets/Images_Upload/2023/02/28/e9682b62-dcd2-4075-b0c0-a2ab5c0018a9.jpg" type="image/jpeg" /></item><item><guid isPermaLink="true">https://www.nieuwsblad.be/cnt/dmf20230228_91133832</guid><link>https://www.nieuwsblad.be/cnt/dmf20230228_91133832</link><title>“Elon Musk is opnieuw rijkste persoon ter wereld”</title><description>&lt;p&gt;Elon Musk heeft zijn titel van rijkste persoon ter wereld heroverd. Dat meldt persagentschap Bloomberg. Musk was midden december onttroond door Fransman Bernard Arnault, maar dankzij de goede prestaties van zijn Tesla-aandelen staat Musk nu opnieuw bovenaan op de Bloomberg Billionaires Index. Op de lijst van Forbes moet Elon Musk het voorlopig wel nog met een tweede plaats stellen.&lt;/p&gt;"</description><pubDate>Tue, 28 Feb 2023 04:00:14 +0100</pubDate><enclosure url="https://static.nieuwsblad.be/Assets/Images_Upload/2023/02/28/1b62165c-a5da-4e6a-948c-bf06606b710f.jpg" type="image/jpeg" /></item></channel></rss>'


class Article:
    def __init__(self):
        self.title = ""
        self.content = ""
        self.author = ""
        self.date = ""
        self.image = ""
        self.tag = []


"""
    scrape RSS article feed
"""


def scrape(feed: string):
    rawData = feed.split("<")
    rawData2 = []
    data: dict = {}
    for i in rawData:
        if len(i) == 0:
            continue
        if i[0] != "/":
            rawData2.append(i)

    for i in rawData2:
        j = i.split(">")
        param = j[0].split(" ")[0]
        paramArg = j[0][len(param):]
        arg = j[1]
        data[param] = (paramArg, arg)
    return data

"""
    link data to an article from HLN
"""

def HLN(data : dict):
    ar = Article()
    if ("title" in data.keys()):
        ar.title = data["title"][1]
    else:
        return None

    if ("description" in data.keys()):
        ar.content = data["description"][1][9:-10]
    else:
        return None

    if ("pubDate" in data.keys()):
        ar.date = data["pubDate"][1]
    else:
        return None

    if ("author" in data.keys()):
        ar.author = data["author"][1]
    else:
        ar.author = ""

    if ("enclosure" in data.keys()):
        ar.image = data["enclosure"][0].split('"')[1]
    else:
        ar.image = ""
    return ar


"""
    interface to scrape RSS Feeds
"""


def rssScraper(rssFeed: string):
    articles: list = []
    rssFeed = HLNFeed
    feed = HLNFeed.split("</description>")
    data = scrape(feed[0])
    data1 = scrape(feed[1])
    tag = data["title"][1].split(":")[1]
    for i in range(1, len(feed)):
        try:
            ar = HLN(scrape(feed[i]))
            if ar == None:
                continue
            ar.tag = tag
            articles.append(ar)
        except:
            pass
    return articles


